select

from community_up
where comment_son_id = #{comment_son_id,jdbcType=INTEGER} and consumer_id = #{consumer_id,jdbcType=INTEGER}

mapper.insert:
insert into community_up (consumer_id, community_id, like_status
  )
values (#{consumerId,jdbcType=INTEGER}, #{communityId,jdbcType=INTEGER}, #{likeStatus,jdbcType=INTEGER}
  )

mapper.update:
update community_up
set consumer_id = #{consumerId,jdbcType=INTEGER},
  community_id = #{communityId,jdbcType=INTEGER},
  like_status = #{likeStatus,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

update community_up
set like_status=#{like_status,jdbcType=INTEGER}
where consumer_id = #{consumer_id,jdbcType=INTEGER} and community_id = #{community_id,jdbcType=INTEGER}

update community_up
set like_status=#{like_status,jdbcType=INTEGER}
where consumer_id = #{consumer_id,jdbcType=INTEGER} and comment_son_id = #{comment_son_id,jdbcType=INTEGER}
